import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../../contexts/CartContext';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { DEFAULT_TEDDY_IMAGE } from '../../utils/cloudinary';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();
  const { currentUser } = useAuth();
  const navigate = useNavigate();

  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageError, setImageError] = useState(false);

  const handleAddToCart = (e) => {
    e.preventDefault();
    e.stopPropagation();

    if (!currentUser) {
      navigate('/login', { state: { from: `/product/${product.id}` } });
      return;
    }

    addToCart(product, 1);
  };

  // Check if the image URL is a base64 string
  const isBase64Image = product.imageUrl && 
    (product.imageUrl.startsWith('data:image') || 
     product.imageProvider === 'base64');

  const handleImageError = () => {
    setImageError(true);
    setImageLoaded(true);
  };

  const handleImageLoad = () => {
    setImageLoaded(true);
  };

  return (
    <div className="product-card">
      <Link to={`/product/${product.id}`} className="product-link">
        <div className="product-image">
          {!imageLoaded && (
            <div className="image-placeholder">
              <div className="loading-spinner"></div>
            </div>
          )}

          <img 
            src={imageError ? DEFAULT_TEDDY_IMAGE : (product.imageUrl || DEFAULT_TEDDY_IMAGE)} 
            alt={product.name}
            onError={handleImageError}
            onLoad={handleImageLoad}
            style={{ display: imageLoaded ? 'block' : 'none' }}
            className={`product-img ${isBase64Image ? 'base64-image' : ''}`}
          />
        </div>

        <div className="product-info">
          <h3 className="product-name">{product.name}</h3>
          <p className="product-price">${(product.price || 0).toFixed(2)}</p>
          <p className="product-description">
            {product.description ? 
              (product.description.length > 60 ? 
                `${product.description.substring(0, 60)}...` : 
                product.description) : 
              'No description available'}
          </p>
        </div>
      </Link>

      <button 
        className="add-to-cart-btn"
        onClick={handleAddToCart}
      >
        Add to Cart
      </button>
    </div>
  );
};

export default ProductCard;
