import React, { useState } from 'react';
import { collection, addDoc, serverTimestamp } from 'firebase/firestore';
import { db } from '../../firebase/config';
import { uploadImageToCloudinary } from '../../utils/cloudinary';
import { toast } from 'react-toastify';
import '../admin/Admin.css';

const AddProduct = () => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    size: '',
    color: '',
    inStock: true
  });

  const [image, setImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setImage(file);
    setPreviewUrl(URL.createObjectURL(file));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.name || !formData.description || !formData.price || !formData.category) {
      toast.error('Please fill all required fields');
      return;
    }

    try {
      setLoading(true);

      // Create a default image data object
      let imageData = {
        url: '/images/default-teddy.jpg',
        publicId: 'default_teddy'
      };

      // Only try to upload if an image is selected
      if (image) {
        try {
          toast.info('Uploading image...', { autoClose: 2000 });
          imageData = await uploadImageToCloudinary(image);

          // Verify the image URL is valid
          if (!imageData.url) {
            throw new Error('Invalid image URL received from upload');
          }
        } catch (imageError) {
          console.error('Image upload failed:', imageError);
          toast.warning('Image upload failed. Using default image instead.');
        }
      }

      // Create product in Firestore with all necessary fields
      const productData = {
        name: formData.name,
        description: formData.description,
        price: parseFloat(formData.price),
        category: formData.category,
        size: formData.size || '',
        color: formData.color || '',
        inStock: formData.inStock,
        imageUrl: imageData.url,
        imagePublicId: imageData.publicId,
        createdAt: serverTimestamp(),
        updatedAt: serverTimestamp()
      };

      await addDoc(collection(db, 'products'), productData);

      toast.success('Product added successfully!');

      // Reset form
      setFormData({
        name: '',
        description: '',
        price: '',
        category: '',
        size: '',
        color: '',
        inStock: true
      });
      setImage(null);
      setPreviewUrl('');

    } catch (error) {
      console.error('Error adding product:', error);
      toast.error('Failed to add product. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="admin-page">
      <div className="container">
        <div className="admin-header">
          <h1>Add New Product</h1>
        </div>

        <div className="admin-content">
          <form className="admin-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="name">Product Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description</label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                required
              ></textarea>
            </div>

            <div className="form-group">
              <label htmlFor="price">Price ($)</label>
              <input
                type="number"
                id="price"
                name="price"
                step="0.01"
                min="0"
                value={formData.price}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="category">Category</label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
                required
              >
                <option value="">Select a category</option>
                <option value="Classic Teddy">Classic Teddy</option>
                <option value="Stuffed Animals">Stuffed Animals</option>
                <option value="Baby Teddy">Baby Teddy</option>
                <option value="Giant Teddy">Giant Teddy</option>
                <option value="Holiday Special">Holiday Special</option>
              </select>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="size">Size</label>
                <input
                  type="text"
                  id="size"
                  name="size"
                  value={formData.size}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label htmlFor="color">Color</label>
                <input
                  type="text"
                  id="color"
                  name="color"
                  value={formData.color}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div className="form-group checkbox">
              <input
                type="checkbox"
                id="inStock"
                name="inStock"
                checked={formData.inStock}
                onChange={handleChange}
              />
              <label htmlFor="inStock">In Stock</label>
            </div>

            <div className="form-group">
              <label htmlFor="image">Product Image</label>
              <input
                type="file"
                id="image"
                accept="image/*"
                onChange={handleImageChange}
              />
              <small>Optional - A default image will be used if none is provided</small>
            </div>

            {previewUrl && (
              <div className="image-preview">
                <img src={previewUrl} alt="Preview" />
              </div>
            )}

            <button 
              type="submit" 
              className="admin-button"
              disabled={loading}
            >
              {loading ? 'Adding Product...' : 'Add Product'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddProduct;
