import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../../contexts/CartContext';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();
  const { currentUser } = useAuth();
  const navigate = useNavigate();

  const fallbackImage = 'https://res.cloudinary.com/davjxvz8w/image/upload/v1653928376/teddy-bears/placeholder_teddy_bear_jmtfdi.jpg';

  const handleAddToCart = (e) => {
    e.preventDefault();
    e.stopPropagation();

    if (!currentUser) {
      navigate('/login', { state: { from: `/product/${product.id}` } });
      return;
    }

    addToCart(product, 1);
  };

  const handleImageError = (e) => {
    e.target.src = fallbackImage;
  };

  return (
    <div className="product-card">
      <Link to={`/product/${product.id}`} className="product-link">
        <div className="product-image">
          <img 
            src={product.imageUrl || fallbackImage} 
            alt={product.name} 
            onError={handleImageError}
          />
        </div>
        <div className="product-info">
          <h3 className="product-name">{product.name}</h3>
          <p className="product-price">${product.price?.toFixed(2) || '0.00'}</p>
          <p className="product-description">
            {product.description?.substring(0, 60) || 'No description'}
            {product.description?.length > 60 ? '...' : ''}
          </p>
        </div>
      </Link>
      <button className="add-to-cart-btn" onClick={handleAddToCart}>
        Add to Cart
      </button>
    </div>
  );
};

export default ProductCard;
